mapper.select:
select * from
(
	select a.*, rownum rnum from
	(
		select * from dailyboard
		where id = #{id}
		
		order by adddate desc
	) a
)
 where rnum >= #{startRow} and rnum <= #{endRow}

mapper.update:
update dailyboard 
set title = #{title}, content = #{content} 
where num = #{num}
