mapper.select:
		select * from
		(
 			select a.*,rownum rnum from 
 			(
  				select * from feedboard
  				where ispost=1
  				
  				order by num desc
  			) a
  		)
		 where rnum>=#{startRow} and rnum<=#{endRow}

		select * from
		(
 			select a.*,rownum rnum from 
 			(
  				select * from feedboard
  				where ispost=1 and cname=#{cname}
  				
  				order by num desc
  			) a
  		)
		 where rnum>=#{startRow} and rnum<=#{endRow}

		select * from
		(
 			select a.*,rownum rnum from 
 			(
  				select * from feedboard
  				where id=#{id}
  				
  				order by num desc
  			) a
  		)
		 where rnum>=#{startRow} and rnum<=#{endRow}

		select * from
		(
 			select a.*,rownum rnum from 
 			(
  				select * from feedboard
  				where id=#{id} and ispost=1
  				
  				order by num desc
  			) a
  		)
		 where rnum>=#{startRow} and rnum<=#{endRow}
